import * as React from 'react'
import {
    List, 
    Datagrid, 
    TextField, 
    ReferenceField, 
    EditButton,
    Edit,
    SimpleForm,
    ReferenceInput,
    SelectInput,
    TextInput,
    Create,
    Filter,
    SimpleList, ImageField
} from 'react-admin'

import {useMediaQuery} from '@material-ui/core'


const IphoneTitle = ({record}) => {
    return <span>Iphone  {record ? `"${record.title}"` : ""} </span>;
}

const IphoneFilter = (props) => (
    <Filter {...props}>
        <TextInput label="Search" source="q" alwaysOn />
        <ReferenceInput label="Type" source="id" reference="type" allowEmpty>
            <SelectInput optionText="name" />
        </ReferenceInput>
    </Filter>
);

export const IphoneList = props => {
    const isSmall = useMediaQuery(theme => theme.breakpoints.down('sm'));
    return(
        <List filters={<IphoneFilter />} {...props}>
            {isSmall ? (
                <SimpleList 
                    primaryText={record => record.title}
                    primaryText={record => `${record.views} views`}
                    tertiaryText={record => new Date(record.published_at).toLocaleString()}
                />
            ): (
                <Datagrid>
                    <TextField source="id" />
                    <ReferenceField source="id" reference="type">
                        <SelectInput source="name" />
                    </ReferenceField>
                    <TextField source="price" />
                    <TextField source="title" />
                    <TextField source="description" />
                    <ImageField source="img" />
                    <EditButton />
                </Datagrid>
            )}
        </List>
    )
}
